(function() {
  angular.module("binderyCurate").directive('binderyFacetValues', [
    '$compile', '$location', function($compile, $location) {
      return {
        restrict: 'E',
        template: '<ul></ul>',
        replace: true,
        scope: {
          values: '=',
          field: '='
        },
        controller: [
          '$scope', '$element', function($scope, $element) {
            return $scope.applyFacetLimit = function(value) {
              var newPath, searchParams;
              searchParams = $location.search();
              searchParams["f[" + $scope.field.code + "_facet][]"] = encode(value);
              console.log(searchParams);
              newPath = $location.path() + "?" + $.param(searchParams, true);
              console.log(decodeURIComponent(newPath));
              return window.location.href = decodeURIComponent(newPath);
            };
          }
        ],
        link: function(scope, element, attrs) {
          return scope.$watch('values', function(newValue, oldValue) {
            var blacklightFacetFieldArray, facetFields;
            if (newValue) {
              blacklightFacetFieldArray = scope.values;
            }
            if (typeof blacklightFacetFieldArray !== "undefined") {
              facetFields = $.map(blacklightFacetFieldArray, function(ff, i) {
                if (i % 2 === 0) {
                  return {
                    value: ff,
                    count: blacklightFacetFieldArray[i + 1]
                  };
                }
              });
            } else {
              facetFields = [];
            }
            element.empty();
            return angular.forEach(facetFields, function(ff) {
              var li, link;
              li = document.createElement('li');
              link = $("<a>", {
                text: ff.value,
                title: ff.value,
                href: "#",
                "ng-click": "applyFacetLimit('" + ff.value + "')"
              }).appendTo(li);
              $("<span class='badge pull-right'>").text(ff.count).appendTo(link);
              $compile(li)(scope);
              return element[0].appendChild(li);
            });
          }, false);
        }
      };
    }
  ]);

}).call(this);
